//连接mongodb
const mongoose = require('mongoose')
const DB_URL = 'mongodb://localhost:27017/app'
// const DB_URL = 'mongodb://127.0.0.1:27017/app'
mongoose.connect(DB_URL)
mongoose.connection.on('connected',function () {
    console.log('mongodb:"app" connect success')
})
// 数据库模型建立
const models = {
    user:{
        'avatarName':{type:String,'require':true,default:'data:image/png;base64,'},// avatar头像名字
        'nickName':{type:String,'require':true},
        'phone':{ type:String,'require':true},
        'pwd':{ type:String,'require':true},// (md5+salt)*2 双层md5加密后的密文
        'gender':{type:String,'require':true,default:''},
        'signature':{type:String,default:'Ta还没有填写个人简介!'},
        'collect':{type:Number,default:0},// 收藏数量
        'focus':{type:Number,default:0},// 关注数量
        'fans':{type:Number,default:0},// 粉丝数量
    },
    chat:{
        'messageListId':{type:String,require:true},// 在消息列表显示的 id
        'send':{type:String,require:true},
        'receive':{type:String,require:true},
        'chatText':{type:String,require:true,default:''},
        'sendTime':{type:Number},// 消息写入数据库的时间
        'isRead':{type:Boolean,default:false},// 是否已读，默认false 只对receive有效
        'showDate':{type:Boolean,default:false},// 是否显示时间 默认不显示聊天的时间
    },
    publish:{
        'publisher':{type:String,require:true},// 发布者 user的_id
        'title':{type:String,require:true},
        'description':{type:String,require:true},
        'price':{type:String,require:true},
        'classification':{type:String,require:true},
        'publishTime':{type:Number},
        'imgList':{type:Object},// 未压缩的原图列表
        'compressImgList':{type:Object},// 压缩后的图片列表
        'clickNum':{type:Number,default:0},// 点击量
        'commentNum':{type:Number,default:0},// 评论量
    },
    collect:{
        'collector':{type:String,require:true},// 收藏者 _id
        'publishDetailsId':{type:String,require:true},// 被收藏宝贝的 _id
        'collectTime':{type:Number},// 收藏时间
    },
    comment:{// 对宝贝的评论
        'publishDetailsId':{type:String,require:true},// 被评论宝贝的 _id
        'commentText':{type:String,require:true,},// 评论的内容
        'commenterId':{type:String,require:true},// 评论者 _id
        // 'commenterNickName':{type:String,require:true},// 评论者 昵称
        // 'commenterAvatar':{type:String,require:true},// 评论者 头像
        'commentObject':{type:String,require:true},// 评论对象 人?宝贝? notPeople isPeople
        'commentObjectId':{type:String,require:true},// 被评论人的 _id
        // 'commentObjectNickName':{type:String,require:true,default:null},// 被评论人的 昵称
        'commentTime':{type:Number,require:true},// 评论时间
        'isRead':{type:Boolean,require:true,default:false},// 是否已读，默认false 只对receive有效

    },
    focus:{// 关注
        'focusId':{type:String,require:true},// 关注者的 _id
        'focusObjectId':{type:String,require:true},// 被关注的 _id
        'focusTime':{type:Number,require:true},// 关注的时间

        // 'avatarName':{type:String,require:true},
        // 'nickName':{type:String,require:true},
        // 'gender':{type:String,require:true},//
        // 'fansNum':{type:String,require:true},//

    },
    fans:{// 粉丝
        'fansId':{type:String,require:true},// 关注者的 _id
        'fansObjectId':{type:String,require:true},// 被关注的 _id
        'fansTime':{type:Number,require:true},// 关注的时间
    }
}
// 实例化模式
for(let m in models){
    mongoose.model(m,new mongoose.Schema(models[m]))
}

module.exports = {
    getModel : function (name) {
        return mongoose.model(name)
    }
}











